import { useState } from "react";

const Prices = ({ bpi }) => {
  // const [list, setList] = useState("");
  let list = "";
  const [currency, setCurrency] = useState("USD");
  console.log("bpi: ", bpi);

  if (currency === "USD") {
    list = (
      <li className="list-group-item">
        Bitcoin rate for {bpi.USD.description}:{" "}
        <span className="badge badge-primary">{bpi.USD.code}</span>
        <strong>{bpi.USD.rate}</strong>
      </li>
    );
  } else if (currency === "GPB") {
    list = (
      <li className="list-group-item">
        Bitcoin rate for {bpi.GPB.description}:{" "}
        <span className="badge badge-primary">{bpi.GPB.code}</span>
        <strong>{bpi.GPB.rate}</strong>
      </li>
    );
  } else if (currency === "EUR") {
    list = (
      <li className="list-group-item">
        Bitcoin rate for {bpi.EUR.description}:{" "}
        <span className="badge badge-primary">{bpi.EUR.code}</span>
        <strong>{bpi.EUR.rate}</strong>
      </li>
    );
  }

  return (
    <div>
      <ul className="list-group">{list}</ul>
      <select onChange={e => setCurrency()}>

      </select>
    </div>
  );
};

export default Prices;
